import React from "react";
import styled from "styled-components";
import Navbar from "../components/Navbar";
import Anouncement from "../components/Anouncement";
//import Product from "../components/Product";
import NewsLetter from "../components/NewsLetter";
import Footer from "../components/Footer";
import { mobile } from "../Responsive";
import { useParams } from "react-router-dom";

function SingleProduct() {
  const { id } = useParams();

  //css section
  const Wrapper = styled.div`
    display: flex;
    /*justify-content: center;
	align-items: center;*/
    ${mobile({ flexDirection: "column" })}

    .imgcontainer {
      flex: 1;
      margin: 20px;
      text-align: center;
    }
    .infocontainer {
      flex: 1;
      padding: 0 50px;
      margin-top: 20px;
      ${mobile({ padding: "0 10px" })}
    }
    img {
      width: 100%;
      object-fit: cover;
      ${mobile({ width: "75%", height: "40vh" })}
    }
    h2 {
    }
    p {
      margin: 20px 0;
      font-weight: 200;
    }

    span {
      font-weight: 200;
      font-size: 50px;
    }
    .filteredContainer {
      width: 80%;
      margin: 20px 0;
      display: flex;
      justify-content: space-between;
      ${mobile({ width: "100%" })}
    }
    .filter {
      display: flex;
      align-items: center;
    }
    .filtertext {
      font-size: 30px;
      font-weight: 300;
      ${mobile({ fontSize: "20px" })}
    }
    .filtercolor {
      width: 30px;
      height: 30px;
      border-radius: 50%;
      background-color: ${(props) => props.color};
      margin: 0 5px;
      cursor: pointer;
    }
    select {
      margin: 5px;
      padding: 5px;
    }
    .addcontainer {
      display: flex;
      align-items: center;
      justify-content: space-between;
      width: 60%;
      margin-bottom: 10px;
    }
    .amountcontainer {
      display: flex;
      font-weight: 700;
      align-items: center;
    }
    .amount {
      display: flex;
      align-items: center;
      justify-content: center;
      width: 30px;
      height: 30px;
      border-radius: 10px;
      margin: 0 5px;
      border: 1px solid teal;
    }
    button {
      padding: 10px;
      background: white;
      border: 1px solid teal;
      cursor: pointer;
    }
    button:hover {
      background: #d6b8b8;
    }
    //
  `;

  return (
    <>
      <Navbar />
      <Anouncement />
      <Wrapper>
        <div className="imgcontainer">
          <img src="/images/gallery4.jpeg" alt="product cover" />
        </div>
        <div className="infocontainer">
          <h2>title</h2>
          <p>
            There are many variations of passages of Lorem Ipsum available but
            the majority have suffered alteration in some form by injected
            humour, or randomised words which dont look even slightly
            believable. If you are going to use a passage of Lorem Ipsumyou need
            to be sure there isnt anything
          </p>
          <span>$20</span>
          <div className="filteredContainer">
            <div className="filter">
              <span className="filtertext">Color</span>
              <div color="red"></div>
              <div color="black"></div>
              <div color="green"></div>
            </div>
            <div className="filter">
              <span className="filtertext">Size</span>
              <select>
                <option>XS</option>
                <option>S</option>
                <option>M</option>
                <option>L</option>
                <option>XL</option>
              </select>
            </div>
          </div>
          <div className="addcontainer">
            <div className="amountcontainer">
              <i className="fa-solid fa-minus"></i>
              <span className="amount">1</span>
              <i className="fa-solid fa-plus"></i>
            </div>
            <button>Add to Cart</button>
          </div>
        </div>
      </Wrapper>
      <NewsLetter />
      <Footer />
    </>
  );
}

export default SingleProduct;
